targets.expr:
topk(${top}, 
    sum by(service_instance_id, url_scheme, http_request_method, http_route, http_response_status_code, error_type) (rate(http_server_request_duration_seconds_count{exported_job="${exported_job}", service_version="${service_version}", service_environment="${service_environment}", service_instance_id=~"${service_instance_id}", http_route=~"${http_route}", http_response_status_code!~"[1-3]..", http_response_status_code=~"${http_response_status_code}"}[$__rate_interval]))
)

topk($top, 
    histogram_quantile(${percentile:value}, 
        sum(rate(http_server_request_duration_seconds_bucket{exported_job="$exported_job", service_environment="$service_environment", service_instance_id=~"${service_instance_id}", http_route=~"${http_route}", http_response_status_code=~"${http_response_status_code}"}[$__rate_interval])) by (le, http_request_method, url_scheme, service_instance_id, http_route, http_response_status_code)
    )
)

topk($top, 
    histogram_quantile(${percentile:value}, 
        sum(rate(http_client_request_duration_seconds_bucket{exported_job="$exported_job", service_environment="$service_environment"}[$__rate_interval])) by (le, http_request_method, url_scheme, http_route, http_response_status_code,server_address,server_port,network_protocol_version)
    )
)
